import React, { useState } from "react";
import { Link } from "react-router-dom";

const AddEditBook = () => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [category, setCategory] = useState("");
  const [isbn, setISBN] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validate form fields
    if (!title || !author || !category || !isbn) {
      setErrorMessage("All fields are required");
      return;
    }

    // Create book object
    const book = {
      title,
      author,
      category,
      isbn,
    };

    // Perform POST or PUT request to the backend API based on the context (add or edit)

    // Clear form fields
    setTitle("");
    setAuthor("");
    setCategory("");
    setISBN("");
    setErrorMessage("");

    // Show success message
    alert("Book successfully added/updated.");
  };

  return (
    <div>
      <h1>{title ? "Edit Book" : "Add a Book"}</h1>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>

        <div>
          <label>Author:</label>
          <input
            type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
          />
        </div>

        <div>
          <label>Category:</label>
          <input
            type="text"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
        </div>

        <div>
          <label>ISBN:</label>
          <input
            type="text"
            value={isbn}
            onChange={(e) => setISBN(e.target.value)}
          />
        </div>

        {/* Display the error message if there is any */}
        {errorMessage && <p>{errorMessage}</p>}

        <button type="submit">{title ? "Edit Book" : "Add a Book"}</button>
      </form>
    </div>
  );
};

export default AddEditBook;
