import React from "react";

const Dashboard = () => {
  const bookData = [
    {
      id: 1,
      title: "Book 1",
      author: "Author 1",
      category: "Category 1",
      isbn: "1234567890",
      createdAt: new Date(),
      modifiedAt: new Date(),
      isActive: true,
    },
  ];

  return (
    <div>
      <div>
        <Filter />
        <button>
          <a href="/add-book">Add a Book</a>
        </button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Book title</th>
            <th>Author name</th>
            <th>Category</th>
            <th>ISBN</th>
            <th>Created At</th>
            <th>Modified/Edited At</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookData.map((book) => (
            <tr key={book.id}>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.category}</td>
              <td>{book.isbn}</td>
              <td>{book.createdAt.toLocaleString()}</td>
              <td>{book.modifiedAt.toLocaleString()}</td>
              <td>
                <button>Edit</button>
                <button>Delete</button>
                <button>{book.isActive ? "Deactivate" : "Re-Activate"}</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const Filter = () => {
  return (
    <select>
      <option value="all">Show All</option>
      <option value="active">Show Active</option>
      <option value="deactivated">Show Deactivated</option>
    </select>
  );
};

export default Dashboard;
