import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const AddEditBook = () => {
  const navigate = useNavigate();

  const [bookData, setBookData] = useState({
    title: "",
    author: "",
    category: "",
    isbn: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setBookData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Perform validation
    if (
      bookData.title.trim() === "" ||
      bookData.author.trim() === "" ||
      bookData.category.trim() === "" ||
      bookData.isbn.trim() === ""
    ) {
      alert("Please fill in all required fields.");
      return;
    }
    // Perform API call or database operation to create/update the book record

    // Show success message
    alert("Book successfully added/updated.");

    // Redirect to Dashboard
    navigate("/dashboard");
  };

  return (
    <div>
      <h1>{bookData.title ? "Edit Book" : "Add a Book"}</h1>

      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Book Title:</label>
          <input
            type="text"
            id="title"
            name="title"
            value={bookData.title}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label htmlFor="author">Author Name:</label>
          <input
            type="text"
            id="author"
            name="author"
            value={bookData.author}
            onChange={handleInputChange}
            required
          />
        </div>

        <div>
          <label htmlFor="category">Category:</label>
          <select
            id="category"
            name="category"
            value={bookData.category}
            onChange={handleInputChange}
            required
          >
            <option value="">Select a category</option>
            <option value="Fiction">Fiction</option>
            <option value="Non-Fiction">Non-Fiction</option>
            <option value="Science">Science</option>
          </select>
        </div>

        <div>
          <label htmlFor="isbn">ISBN:</label>
          <input
            type="number"
            id="isbn"
            name="isbn"
            value={bookData.isbn}
            onChange={handleInputChange}
            required
          />
        </div>

        <button type="submit">
          {bookData.title ? "Edit Book" : "Add a Book"}
        </button>
      </form>

      <p>
        <Link to="/dashboard">Back to Dashboard</Link>
      </p>
    </div>
  );
};

export default AddEditBook;
